ClientSideValidations.selectors.validate_inputs += ', .select2-container:visible ~ :input:enabled[data-validate]';

window.connect_group_to_select = function(group, select) {
  var select_button, selected_button;
  selected_button = null;
  $(group).find("button").click(function(e) {
    e.preventDefault();
    return select_button(this);
  });
  select_button = function(btn) {
    select.val($(btn).data('value').toString());
    if (selected_button) {
      selected_button.removeClass("active button-green").addClass("button-grey");
    }
    $(btn).addClass("active button-green").removeClass("button-grey");
    return selected_button = $(btn);
  };
  if (select.val()) {
    return select_button(select.siblings("[data-value='" + select.val() + "']"));
  }
};

$(function() {
  $(".subpages").click(function() {
    var page;
    page = $(this).attr("page");
    return $("#submenus_" + page).slideToggle("slow");
  });
  $("#related_field_of_study").autocomplete({
    source: "/home/autocomplete",
    select: function(event, ui) {
      return $("#related_field_of_study").val(ui.item.id);
    }
  });
  return $("#search-field-study").click(function(event) {
    if ($("#q_address_cont").val().length === 0 && $("#related_field_of_study").val().length === 0) {
      return event.preventDefault();
    }
  });
});
